$.get("https://students-api.2.us-1.fl0.io/movies",(function(t){const r=$("#movieContainer"),n=t.map((t=>$("<div>").addClass("card").html(`\n                <img src="${t.poster}" alt="${t.title}" class="cardImg">\n                <div class="cardContainer">\n                    <h5 class="cardTitle">${t.title} (${t.year})</h5>\n                    <p class="cardText"><strong>Director:</strong> ${t.director}</p>\n                    <p class="card-text"><strong>Duration:</strong> ${t.duration}</p>\n                    <p class="card-text"><strong>Genre:</strong> ${t.genre}</p>\n                    <p class="card-text"><strong>Rate:</strong> ${t.rate}</p>\n                </div>\n            `)));r.empty().append(n)})).fail((function(t){console.error("Error fetching movies:",t)}));